function addItem(){$("#add-item-error").hide();var n=$("#add-item-title").val();$.post("/Todo/AddItem",{title:n},function(){window.location="/Todo"}).fail(function(n){if(n&&n.responseJSON){var t=n.responseJSON[Object.keys(n.responseJSON)[0]];$("#add-item-error").text(t);$("#add-item-error").show()}})}function markCompleted(n){n.disabled=!0;$.post("/Todo/MarkDone",{id:n.name},function(){var t=n.parentElement.parentElement;$(t).addClass("done")})}$(document).ready(function(){$("#add-item-button").on("click",addItem);$(".done-checkbox").on("click",function(n){markCompleted(n.target)})});